import React from "react";
import Link from "next/link";
import HeaderStyles from "./SiteHeader.module.scss";
// import { unstable_renderSubtreeIntoContainer } from "react-dom";

class Navigation extends React.Component {
  state = {
    isOpen: true,
    isSideMenuOpen: false,
    hasScrolled: false,
  };

  componentDidMount() {
    window.addEventListener("scroll", () => {
      if (window.scrollY > 20) {
        this.setState({ hasScrolled: true });
      }
      if (window.scrollY < 20) {
        this.setState({ hasScrolled: false });
      }
    });
  }

  handleClick = () => {
    this.setState({
      isOpen: !this.state.isOpen,
      isSideMenuOpen: !this.state.isSideMenuOpen,
    });
  };
  render() {
    return (
      <header
        className={
          this.state.hasScrolled
            ? "HeaderStyles.SiteHeader"
            : "HeaderStyles.SiteHeader__init"
        }
      >
        <div
          className={
            this.state.isOpen
              ? "HeaderStyles.SiteHeader__menu-icon"
              : "HeaderStyles.SiteHeader__menu-icon HeaderStyles.SiteHeader__menu-icon--close-x"
          }
          id="menu-icon"
          onClick={this.handleClick}
        >
          <div className="HeaderStyles.SiteHeader__menu-icon__middle"></div>
        </div>

        <div
          id="side-menu"
          className={
            this.state.isSideMenuOpen ? "side-nav open-side-menu" : "side-nav"
          }
        >
          <ul className="side-nav-list-items">
            <Link href="/portfolio">
              <a
                className="HeaderStyles.SiteHeader__listitem"
                onClick={this.handleClick}
              >
                Portfolio
              </a>
            </Link>
            <Link href="/contact">
              <a
                className="HeaderStyles.SiteHeader__listitem"
                onClick={this.handleClick}
              >
                Contact
              </a>
            </Link>
          </ul>
        </div>
        <div className="content-sizer">
          <nav className="HeaderStyles.SiteHeader__nav">
            <ul className="HeaderStyles.SiteHeader__nav-list">
              <div className="HeaderStyles.SiteHeader__brand-wrapper">
                <Link href="/">
                  <div
                    className={
                      this.state.hasScrolled
                        ? "HeaderStyles.SiteHeader__brand-name"
                        : "HeaderStyles.SiteHeader__brand-name-init"
                    }
                  >
                    <div className="HeaderStyles.SiteHeader__brand-wrapper__line"></div>
                    <div className="HeaderStyles.SiteHeader__brand-name__the">
                      The
                    </div>
                    <div className="HeaderStyles.SiteHeader__brand-name__fine-web">
                      Fine Web
                    </div>
                  </div>
                </Link>
              </div>
              <div className="HeaderStyles.SiteHeader__list-item-wrapper">
                <li className="HeaderStyles.SiteHeader__nav-list-item">
                  <Link href="/portfolio">
                    <a className="HeaderStyles.SiteHeader__listitem">
                      Portfolio
                    </a>
                  </Link>
                </li>
                <li>
                  <Link href="/contact">
                    <a className="HeaderStyles.SiteHeader__listitem">Contact</a>
                  </Link>
                </li>
              </div>
            </ul>
          </nav>
        </div>
      </header>
    );
  }
}

export default Navigation;
