// FORMSPREE ENDPOINT:  https://formspree.io/xyynberj

import React, { useState } from "react";
import axios from "axios";
import styles from "./contact.module.scss";

export default () => {
  const [status, setStatus] = useState({
    submitted: false,
    submitting: false,
    info: { error: false, msg: null },
  });
  const [inputs, setInputs] = useState({
    email: "",
    message: "",
  });
  const handleServerResponse = (ok, msg) => {
    if (ok) {
      setStatus({
        submitted: true,
        submitting: false,
        info: { error: false, msg: msg },
      });
      setInputs({
        email: "",
        message: "",
      });
    } else {
      setStatus({
        info: { error: true, msg: msg },
      });
    }
  };
  const handleOnChange = (e) => {
    e.persist();
    setInputs((prev) => ({
      ...prev,
      [e.target.id]: e.target.value,
    }));
    setStatus({
      submitted: false,
      submitting: false,
      info: { error: false, msg: null },
    });
  };
  const handleOnSubmit = (e) => {
    e.preventDefault();
    setStatus((prevStatus) => ({ ...prevStatus, submitting: true }));
    axios({
      method: "POST",
      url: "https://formspree.io/xyynberj",
      data: inputs,
    })
      .then((response) => {
        handleServerResponse(
          true,
          "Thank you, your message has been submitted.",
        );
      })
      .catch((error) => {
        handleServerResponse(false, error.response.data.error);
      });
  };
  return (
    <main className={styles.contactFormMain}>
      <div className={styles.formWrapper}>
        <form onSubmit={handleOnSubmit} className={styles.contactForm}>
          <label htmlFor="name" className={styles.contactFormLabel}>
            Name
          </label>
          <input
            id="name"
            type="name"
            name="_replyto"
            onChange={handleOnChange}
            required
            value={inputs.name}
            className={styles.contactFormInput}
          />
          <label htmlFor="phone" className={styles.contactFormLabel}>
            Phone
          </label>
          <input
            id="phone"
            type="phone"
            name="_replyto"
            onChange={handleOnChange}
            required
            value={inputs.phone}
            className={styles.contactFormInput}
          />

          <label htmlFor="email" className={styles.contactFormLabel}>
            Email
          </label>
          <input
            id="email"
            type="email"
            name="_replyto"
            onChange={handleOnChange}
            required
            value={inputs.email}
            className={styles.contactFormInput}
          />
          <label htmlFor="message" className={styles.contactFormLabel}>
            Message
          </label>
          <textarea
            id="message"
            name="message"
            onChange={handleOnChange}
            required
            value={inputs.message}
            className={styles.contactFormTextArea}
          />
          <button
            type="submit"
            disabled={status.submitting}
            className={styles.contactFormBtn}
          >
            {!status.submitting
              ? !status.submitted ? "Submit!!" : "Submitted"
              : "Submitting..."}
          </button>
        </form>
        {status.info.error && (
          <div className={styles.error}>Error: {status.info.msg}</div>
        )}
        {!status.info.error && status.info.msg && <p>{status.info.msg}</p>}
      </div>
    </main>
  );
};
