import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

const Site = ({ site }) => (
  <Card style={{ width: "18rem" }} key={site.name}>
    <Card.Img variant="top" src={site.img} />
    <Card.Body>
      <Card.Title>{site.name}</Card.Title>
      <Card.Text className="card-text">{site.description}</Card.Text>
      <Button variant="primary">
        <a href={site.link} target="_blank">
          VIEW WEBSITE
        </a>
      </Button>
    </Card.Body>
    <style jsx>{`
      .card-text {
        font-size: 16px;
        color: red;
      }
    `}</style>
  </Card>
);

export default Site;
