import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

const Site = ({ site }) => (
  <Card style={{ width: "18rem", textAlign: "center" }} key={site.name}>
    <Card.Img variant="top" src={site.img} />
    <Card.Body>
      <h4 className="card-title">{site.name}</h4>
      <p className="card-text">{site.description}</p>
      <Button variant="outline-primary">
        <a href={site.link} target="_blank" className="card-button">
          VIEW WEBSITE
        </a>
      </Button>
    </Card.Body>
    <style jsx>{`
      .card-button {
        font-size: 16px;
      }
      ,
      .card-text {
        font-size: 16px;
        color: #666;
        // text-align: center;
      }
      ,
      .card-title {
        font-size: 20px;
        text-align: center;
      }
    `}</style>
  </Card>
);

export default Site;
