import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import styles from "./Website.module.scss";

const Site = ({ website }) => (
  <>
    <div className="mycard">
      <Card
        style={{
          width: "22rem",
          textAlign: "center",
          boxShadow: "2px 2px 5px 5px #eee",
          margin: "20px",
          borderRadius: ".55em",
        }}
        key={website.name}
      >
        <div className="inner-card">
          <Card.Img variant="top" src={website.img} />
          <Card.Body>
            <h4 className="card-title">{website.name}</h4>
            <p className="card-text">{website.description}</p>
            <Button
              variant="outline-primary"
              href={website.link}
              target="_blank"
            >
              VIEW WEBSITE
            </Button>
          </Card.Body>
        </div>
      </Card>
    </div>
  </>
);

export default Site;
